/*
 * Function Name: InitMFRC522
 * Description: Initialize RC522
 * Input: None
 * Return value: None
*/
void MFRC522_Init(void)
{
    
  MFRC522_RESET_PIN = 0;
  myDelay(10);  // delay 10ms   
  MFRC522_RESET_PIN = 1;
  //NRSTPD = 1;// reset pin high
//Open SPI1
  
    if (!SPI1_Open(0))
    {
        LATDbits.LATD10 = 0;     // turn on Led
    }
    
  
  MFRC522_Reset();
  myDelay(10);

  //Timer: TPrescaler*TreloadVal/6.78MHz = 24ms
  PCD_WriteRegister(TModeReg, 0x8D);                //Tauto=1; f(Timer) = 6.78MHz/TPreScaler
  PCD_WriteRegister(TPrescalerReg, 0x3E);        //TModeReg[3..0] + TPrescalerReg
  PCD_WriteRegister(TReloadRegL, 30);
  PCD_WriteRegister(TReloadRegH, 0);

  PCD_WriteRegister(TxASKReg, 0x40);                //100%ASK
  PCD_WriteRegister(ModeReg, 0x3D);                //CRC Initial value 0x6363        ???
  
    // Enable RX interrupt
 MFRC522_EnableInterrupt();
  

  //ClearBitMask(Status2Reg, 0x08);                //MFCrypto1On=0
  //Write_MFRC522(RxSelReg, 0x86);                //RxWait = RxSelReg[5..0]
  //Write_MFRC522(RFCfgReg, 0x7F);                   //RxGain = 48dB

  AntennaOn();                //Open the antenna
}

uint8_t MFRC522_DetectTag(void)
{
    uint8_t result, i;
    
    result = MI_NOTAGERR;
    if (MFRC522_Request(PICC_REQIDL, tagType) == 0) 
        {
            if (MFRC522_Anticoll(serNum) == 0) 
            {
                // A tag is detected, and its serial number (ID) is read
                for (i=0; i<5; i++)
                {
                    pgv->tagSN[i] = serNum[i]; // Transfer SN
                }
                result = MI_OK;
            }
        } 
    return result;
}

void MFRC522_EnableInterrupt(void) 
{
    // Enable Idle interrupt and Timer interrupt
    PCD_WriteRegister(ComIEnReg, (1 << 1) | (1 << 7)); // IdleIEn = 1, TimerIEn = 1
    // Enable LoAlertIEn and HiAlertIEn interrupts
    PCD_WriteRegister(DivIEnReg, (1 << 2) | (1 << 3)); // HiAlertIEn = 1, LoAlertIEn = 1
    // Clear the interrupt request flags
    PCD_WriteRegister(ComIrqReg, 0x7F);  // Clear all flags
    // Enable IRQ pin output (optional)
    PCD_WriteRegister(ComIrqReg, 0x80);  // IRQ pin output enabled
}



void PCD_WriteRegister(	uint8_t reg, uint8_t data ) 
{
    uint8_t address = (reg << 1) & 0x7E;
    // Set the chip select line low to start communication
    // Select the sensor (pull CS low)
    LATCbits.LATC3 = 0; // Select the sensor (CS low)
    
    // Send the register address with write operation (MSB = 0)
    SPI1_ByteExchange(address);
    
    // Send the data byte to be written to the register
    SPI1_ByteExchange(data);
    
    // Deselect the sensor (pull CS high)
    LATCbits.LATC3 = 1; //
    shortDelay(100);    // approximate delay Fosc cycles
}

uint8_t PCD_ReadRegister( uint8_t reg ) 
{
    
    uint8_t id;
    uint8_t address = ((reg << 1) & 0x7E) | 0x80;
    LATCbits.LATC3 = 0; // Select the sensor (CS low)

    SPI1_ByteExchange(address); // Send read command
    id = SPI1_ByteExchange(0x00); // Send dummy data for clocks, Read data
    
    LATCbits.LATC3 = 1; // Deselect the sensor (CS high)
    shortDelay(100);    // approximate delay Fosc cycles

    return id;
}

